        s->next=L->next;
        L->next=s;
        cin>>e;
    }
    return L;
}

void print(LinkList list)
{
    LinkList e;
    e=list->next;
    if(!e)
    {
        cout<<"NULL";
    } else {
        while (e->next) {
            cout << e->data << " ";
            e = e->next;
        }
        cout << e->data;
    }
}
void merge(LinkList L1,LinkList L2,LinkList &L3)
{
    LinkList l1=L1->next,l2=L2->next,l3;
    l3=L3=L1;
    while (l1&&l2)
    {
        if (l1->data<=l2->data)
        {
            l3->next=l1;
            l3=l1;
            l1=l1->next;
        } else
        {
            l3->next=l2;
            l3=l2;
            l2=l2->next;
        }
    }
    l3->next=l1?l1:l2;
}
int main()
{
    LinkList L1,L2,L3;
    L1=endCreate();
    L2=endCreate();
    merge(L1,L2,L3);
    print(L3);
}
```

这道题两点重点，第一是实现merge操作。传进去两个链表和第三个的引用，然后一直判断直到某一个结束，如果L3后边是L1说明L1还有剩下的，如果不是说明剩下的都是L2后边；

第二个是输出，需要注意如果是空就输出NULL，他是让打印出NULL而不是return。。。

