```
#include<iostream>
using namespace std;

#define MAXQSIZE 100
#define OK 1
#define ERROR 0
#define OVERFLOW -2
typedef int QElemType;
typedef char SElemType;
typedef int Status;

    typedef struct {
        QElemType *base;
        int front;
        int rear;
    } SqQueue;

class MyQueue {

public:
    Status EnQueue(SqQueue &Q, QElemType e) {
        if (
                Q.front == (Q.rear + 1) % MAXQSIZE
                )
            return ERROR;
        Q.base[Q.rear] = e;

        Q.rear = (Q.rear + 1) % MAXQSIZE;
        return OK;
    }


    Status DeQueue(SqQueue &Q) {
        if (
                Q.front == Q.rear
                ) {
            cout << "Invalid" << endl;
            return 0;
        }
        int e = Q.base[Q.front];

        Q.front = (Q.front + 1) % MAXQSIZE;
        cout<<e<<endl;
    }
    Status size(SqQueue &Q)
    {
        return(Q.rear-Q.front+MAXQSIZE)%MAXQSIZE;
    }


    Status InitQueue(SqQueue &Q) {

        Q.base = new QElemType[MAXQSIZE];
        if (!Q.base)
            exit(OVERFLOW);

        Q.rear = Q.front = 0;
        return OK;
    }
};

int main() {
    SqQueue q;
    MyQueue q1;
    q1.InitQueue(q);
