#include<stdio.h>
int main()
{
    int a,b,alist[999],blist[999];
    scanf("%d%d",&a,&b);
    for(int i=0;i<a;i++){
        scanf("%d",&alist[i]);
    }
    for(int j=0;j<b;j++){
        scanf("%d",&blist[j]);
