#include<iostream>
#include <stack>
#include <string>
#include <queue>
using namespace std;
int main()
{
    stack<char>s;
    string str;
    queue<char>Q;
    char x;
    cin>>str;
    for (int i = 0; str[i]!='\0'; ++i) {
        s.push(str[i]);
    }
    cin>>x;
    cout<<s.size()<<" "<<s.top()<<endl;
    while(!s.empty()) {
        cout<<s.top();
        if(s.top()!=x)
        {
            Q.push(s.top());
        }
        s.pop();
    }
    cout<<endl;
    cout<<Q.size()<<" "<<Q.front()<<" "<<Q.back()<<endl;
    while (!Q.empty())
    {
        cout<<Q.front();
        Q.pop();
    }
}
```

